    return __balances[sender, to]


@__export('currency')
def transfer_from(amount: float, to: str, main_account: str):
    assert amount > 0, 'Cannot send negative balances!'
    sender = ctx.caller
    assert __balances[main_account, sender
        ] >= amount, 'Not enough coins approved to send! You have {} and are trying to spend {}'.format(
        __balances[main_account, sender], amount)
    assert __balances[main_account] >= amount, 'Not enough coins to send!'
    __balances[main_account, sender] -= amount
    __balances[main_account] -= amount
    __balances[to] += amount

import currency
import election_house
__candidate_state = Hash(contract='elect_masternodes', name='candidate_state')
__top_candidate = Variable(contract='elect_masternodes', name='top_candidate')
__no_confidence_state = Hash(contract='elect_masternodes', name=
    'no_confidence_state')
__last_candidate = Variable(contract='elect_masternodes', name='last_candidate'
    )
__to_be_relinquished = Variable(contract='elect_masternodes', name=
    'to_be_relinquished')
STAMP_COST = 20
__member_cost = Variable(contract='elect_masternodes', name='member_cost')
__controller = Variable(contract='elect_masternodes', name='controller')


def ____(policy: str='members', cost: int=500000):
    __controller.set(policy)
    __member_cost.set(cost)


@__export('elect_masternodes')
def register():
    assert not __candidate_state['registered', ctx.caller
        ], 'Already registered.'
    currency.transfer_from(__member_cost.get(), ctx.this, ctx.caller)
    __candidate_state['registered', ctx.caller] = True
    __candidate_state['votes', ctx.caller] = 0
    if __top_candidate.get() is None:
        __top_candidate.set(ctx.caller)


@__export('elect_masternodes')
def unregister():
    mns = election_house.current_value_for_policy(__controller.get())
    assert __candidate_state['registered', ctx.caller], 'Not registered.'
    assert ctx.caller not in mns, "Can't unstake if in governance."
    currency.transfer(__member_cost.get(), ctx.caller)
    __candidate_state['registered', ctx.caller] = False
    __candidate_state['votes', ctx.caller] = 0


@__export('elect_masternodes')
def vote_candidate(address: str):
    assert __candidate_state['registered', address
        ], 'Candidate not registered.'
    v = __candidate_state['last_voted', ctx.caller]
    assert v is None or now - v > datetime.DAYS * 1, 'Voting again too soon.'
    vote_cost = STAMP_COST / election_house.current_value_for_policy(
        'stamp_cost')
    currency.transfer_from(vote_cost, 'blackhole', ctx.caller)
    __candidate_state['last_voted', ctx.caller] = now
    votes = __candidate_state['votes', address]
    if votes is None:
        __candidate_state['votes', address] = 1
    else:
        __candidate_state['votes', address] += 1
    if __top_candidate.get() is not None:
        if __candidate_state['votes', address] > __candidate_state['votes',
            __top_candidate.get()]:
            __top_candidate.set(address)


@__export('elect_masternodes')
def top_member():
    return __top_candidate.get()


@__export('elect_masternodes')
def pop_top():
    assert ctx.caller == __controller.get(), 'Wrong smart contract caller.'
    top = top_member()
    if top is None:
        return None
    __candidate_state.clear('votes')
    __top_candidate.set(None)


@__export('elect_masternodes')
def vote_no_confidence(address: str):
    assert address in election_house.current_value_for_policy(__controller.
        get()), 'Cannot vote against a non-committee member'
    v = __no_confidence_state['last_voted', ctx.caller]
    assert v is None or now - v > datetime.DAYS * 1, 'Voting again too soon.'
    vote_cost = STAMP_COST / election_house.current_value_for_policy(
        'stamp_cost')
    currency.transfer_from(vote_cost, 'blackhole', ctx.caller)
    __no_confidence_state['last_voted', ctx.caller] = now
    if __no_confidence_state['votes', address] is None:
        __no_confidence_state['votes', address] = 1
    else:
        __no_confidence_state['votes', address] += 1
    if __last_candidate.get() is None:
        __last_candidate.set(address)
    elif __no_confidence_state['votes', address] > __no_confidence_state[
        'votes', __last_candidate.get()]:
        __last_candidate.set(address)


@__export('elect_masternodes')
def last_member():
    r = __to_be_relinquished.get()
    if r is not None:
        return r
    return __last_candidate.get()


@__export('elect_masternodes')
def pop_last():
    assert ctx.caller == __controller.get(), 'Wrong smart contract caller.'
    r = __to_be_relinquished.get()
    if r is not None:
        __no_confidence_state['votes', r] = 0
        __to_be_relinquished.set(None)
    else:
        __no_confidence_state.clear('votes')
        __candidate_state['registered', __last_candidate.get()] = False
        __last_candidate.set(None)


@__export('elect_masternodes')
def force_removal(address: str):
    assert ctx.caller == __controller.get(), 'Wrong smart contract caller.'
    __candidate_state['registered', address] = False


@__export('elect_masternodes')
def relinquish():
    assert ctx.caller in election_house.current_value_for_policy(__controller
        .get())
    r = __to_be_relinquished.get()
    assert r is None, 'Someone is already trying to relinquish!'
    __to_be_relinquished.set(ctx.caller)

