INTRODUCE_MOTION = 'introduce_motion'
VOTE_ON_MOTION = 'vote_on_motion'
NO_MOTION = 0
REMOVE_MEMBER = 1
ADD_SEAT = 2
REMOVE_SEAT = 3
VOTING_PERIOD = datetime.DAYS * 1
__S = Hash(contract='masternodes', name='S')
__minimum_nodes = Variable(contract='masternodes', name='minimum_nodes')
__candidate_contract = Variable(contract='masternodes', name=
    'candidate_contract')


def ____(initial_members: list, minimum: int=1, candidate: str='elect_members'
    ):
    __S['members'] = initial_members
    __minimum_nodes.set(minimum)
    __candidate_contract.set(candidate)
    __S['yays'] = 0
    __S['nays'] = 0
    __S['current_motion'] = NO_MOTION
    __S['motion_opened'] = now


@__export('masternodes')
def quorum_max():
    return int(len(__S['members']) * 2 / 3) + 1


@__export('masternodes')
def quorum_min():
    return min(quorum_max(), __minimum_nodes.get())


@__export('masternodes')
def current_value():
    return __S['members']


@__export('masternodes')
def vote(vk: str, obj: list):
    assert isinstance(obj, list), 'Pass a list!'
    arg = None
    if len(obj) == 3:
        action, position, arg = obj
    else:
        action, position = obj
    __assert_vote_is_valid(vk, action, position, arg)
    if action == INTRODUCE_MOTION:
        __introduce_motion(position, arg)
    else:
        assert __S['current_motion'] != NO_MOTION, 'No motion proposed.'
        if now - __S['motion_opened'] >= VOTING_PERIOD:
            __reset()
        assert __S['positions', vk] is None, 'VK already voted.'
        if position is True:
            __S['yays'] += 1
            __S['positions', vk] = position
        else:
            __S['nays'] += 1
            __S['positions', vk] = position
        if __S['yays'] >= len(__S['members']) // 2 + 1:
            __pass_current_motion()
            __reset()
        elif __S['nays'] >= len(__S['members']) // 2 + 1:
            __reset()


def __assert_vote_is_valid(vk: str, action: str, position: bool, arg: Any=None
    ):
    assert vk in __S['members'], 'Not a member.'
    assert action in [INTRODUCE_MOTION, VOTE_ON_MOTION], 'Invalid action.'
    if action == INTRODUCE_MOTION:
        assert __S['current_motion'] == NO_MOTION, 'Already in motion.'
        assert 0 < position <= REMOVE_SEAT, 'Invalid motion.'
        if position == REMOVE_MEMBER:
            __assert_vk_is_valid(arg)
    elif action == VOTE_ON_MOTION:
        assert isinstance(position, bool), 'Invalid position'


def __assert_vk_is_valid(vk: str):
    assert vk is not None, 'No VK provided.'
    assert isinstance(vk, str), 'VK not a string.'
    assert len(vk) == 64, 'VK is not 64 characters.'
    int(vk, 16)


def __introduce_motion(position: int, arg: Any):
    assert position <= REMOVE_SEAT, 'Invalid position.'
    if position == REMOVE_MEMBER:
        assert arg in __S['members'], 'Member does not exist.'
        assert len(__S['members']) > __minimum_nodes.get(
            ), 'Cannot drop below current quorum.'
        __S['member_in_question'] = arg
    __S['current_motion'] = position
    __S['motion_opened'] = now


def __pass_current_motion():
    current_motion = __S['current_motion']
    members = __S['members']
    if current_motion == REMOVE_MEMBER:
        members.remove(__S['member_in_question'])
    elif current_motion == ADD_SEAT:
        member_candidates = importlib.import_module(__candidate_contract.get())
        new_mem = member_candidates.top_member()
        if new_mem is not None:
            members.append(new_mem)
            member_candidates.pop_top()
    elif current_motion == REMOVE_SEAT:
        member_candidates = importlib.import_module(__candidate_contract.get())
        old_mem = member_candidates.last_member()
        if old_mem is not None:
            members.remove(old_mem)
            member_candidates.pop_last()
    __S['members'] = members


def __reset():
    __S['current_motion'] = NO_MOTION
    __S['member_in_question'] = None
    __S['yays'] = 0
    __S['nays'] = 0
    __S.clear('positions')
